INSERT INTO `san_pham` (`id_sp`, `menu_id`, `ten`, `code`, `thong_tin_sp`, `ban_chay`, `moi_ve`, `gia_ban`, `gia_giam_gia`, `giam_gia`, `anh_dai_dien`, `anh_ct`, `anh_ct2`, `ngay_dang`, `ngay_sua`, `so_luong`, `an_hien`) VALUES
(1, 8, 'Nhẫn cưới Delicat NC 705 (Giá 1 đôi)', 'nha-cuoi-delicat-nc-705', '<p>M&atilde; sản phẩm: NC705 - 14K Nằm trong BST nhẫn cưới cao cấp của Huy Thanh Jewelry, mỗi thiết kế &quot;Delicat&quot; khơi gợi c&acirc;u chuyện t&igrave;nh y&ecirc;u l&atilde;ng m&atilde;n, say đắm từ Paris hoa lệ với điểm nhấn đặc biệt từ c&ocirc;ng nghệ mo l&ograve;ng nhẫn t&acirc;n tiến, tạo đường n&eacute;t tinh xảo</p>\r\n', 1, 1, 5000000, 0, 0, '705-1_36a1b7f75bb54d7e877b77e9102e3650_compact-removebg-preview.png', '705-2_9bc3947ed9134e6c92c2f10144c4b224_compact-removebg-preview.png', '705-4_05f18e2d3e544297ac5bfce2409469af_compact-removebg-preview.png', '2022-01-19', '0000-00-00', 0, 0),
(2, 8, 'Nhẫn cưới NC 704 (Giá 1 đôi)', 'nha-cuoi-nc-704', '<p>M&atilde; sản phẩm: NC704 - 14K Nhẫn cưới NC 704 mang thiết kế đặc biệt từ c&ocirc;ng nghệ mo l&ograve;ng nhẫn t&acirc;n tiến, tạo đường n&eacute;t tinh xảo v&agrave; mềm mại so với kiểu d&aacute;ng truyền thống. Sự kết hợp tinh tế giữa 2 chất liệu v&agrave;ng trắng - v&agrave;ng hồng, cặp nhẫn vừa sang trọ</p>\r\n', 1, 0, 11000000, 10000000, 1, 'nc704_1_5b85df2305ed446fa0cb1f3076829acf_master.png', 'nc704_3_3cdbe9bd7bcd4f3a9efe794762d6171f_master.png', 'nc704_3_3cdbe9bd7bcd4f3a9efe794762d6171f_master.png', '2022-01-19', '0000-00-00', 50, 0),
(3, 8, 'Nhẫn cưới Beyond Love NC 756', 'nhan-cuoi-beyond-love-nc-756', '<p>M&atilde; sản phẩm: NC756 - 18K - Moissanite Nhẫn cưới Beyond Love NC756 mang thiết kế tối giản nhất trong BST Đai nhẫn giữ d&aacute;ng vu&ocirc;ng vắn, nhẫn nam tối giản với điểm nhấn tinh tế từ 3 vi&ecirc;n đ&aacute; mỗi b&ecirc;n viền trong khi nhẫn nữ rất lộng lẫy nhờ được nạm đ</p>\r\n', 0, 0, 12000000, 0, 0, '705-1_36a1b7f75bb54d7e877b77e9102e3650_compact-removebg-preview.png', '705-2_9bc3947ed9134e6c92c2f10144c4b224_compact-removebg-preview.png', '705-4_05f18e2d3e544297ac5bfce2409469af_compact-removebg-preview.png', '2022-01-20', '0000-00-00', 50, 0),
(4, 9, 'Bộ Trang Sức Purple Secret PTB 290', 'bo-trang-suc-purple-secert-ptb-290', '<p>Purple Secret nổi bật với chi tiết đ&aacute; m&agrave;u t&iacute;m Amethyst Tuy mang sắc t&iacute;m quyến rũ v&agrave; đầy b&iacute; ẩn thế nhưng đ&aacute; m&agrave;u amethyst được người xưa tin rằng đem đến sự tỉnh t&aacute;o v&agrave; c&acirc;n bằng trong cuộc sống</p>\r\n', 1, 0, 12000000, 11000000, 1, 'fl_ptb309.1-03_1cf265a677f14a93a9c41a5ab2481fe4_compact.jpg', 'wb_dcptb290_3c8268caf8b743c69a21b36825041845_compact-removebg-preview.png', 'wb_nptb290_2_661f26b0a9cd435bad6388558d825639_compact-removebg-preview.png', '2022-01-19', '0000-00-00', 50, 0),
(5, 9, 'Bộ Trang Sức Blue Sun PTB 292', 'bo-trang-suc-blue-sun-ptb-292', '<p>M&atilde; sản phẩm: PTB 292 - 14K Mang vẻ đẹp lộng lẫy v&agrave; b&iacute; ẩn nhất, Blue Sun sở hữu vi&ecirc;n đ&aacute; chủ m&agrave;u Sapphire xanh dương đầy m&ecirc; hoặc. Người xưa quan niệm rằng, đ&aacute; m&agrave;u xanh sapphire tượng trưng cho sự chung thủy v&agrave; niềm tin vững chắc.</p>\r\n', 0, 0, 16000000, 0, 0, 'fl_ptb309.1-05_899c2c1ba0914eb7b18a9e5908cca8fb_compact.jpg', 'wb_dcptb292__1__06b53800e35f486abbe8f3db06444013_compact-removebg-preview.png', 'wb_nptb292_2_b2e2ef93e9a249f28a109c39fdc5c4f8_compact-removebg-preview.png', '2022-01-19', '0000-00-00', 50, 0),
(7, 9, 'Bộ trang sức PTB 280', 'bo-trang-suc-ptb-280', '<p>M&atilde; sản phẩm: PTB280 - 14K Bộ trang sức PTB 280 nổi bật đầy sức h&uacute;t với những vi&ecirc;n đ&aacute; CZ s&aacute;ng lấp l&aacute;nh. Sản phẩm được chế t&aacute;c chất liệu v&agrave;ng v&agrave;ng ph&ugrave; hợp với mọi tone da để c&aacute;c c&ocirc; g&aacute;i lu&ocirc;n tự tin, xinh đẹp, tỏa nắng v&agrave; đ&oacute;n nhận năng lượng t&iacute;ch cực</p>\r\n', 1, 1, 15000000, 0, 0, 'b5_cf1e74f7eb1045528b7067f84196109e_master.webp', 'wb_dcptb280_967620eeb8be4b47bc5ad5952526bb3d_compact-removebg-preview.png', 'wb_btptb280b_dd613a467e034bddb091838e45bb7805_compact-removebg-preview.png', '2022-01-19', '0000-00-00', 50, 0),
(8, 10, 'Bộ Trang Sức Printemps PTB 135', 'bo-trang-suc-printemps-ptb-135', '<p>M&atilde; sản phẩm: PTB135 Bộ Trang Sức Printemps PTB 135 với h&agrave;ng loạt mẫu thiết kế trang sức tinh xảo được lấy cảm hứng chủ đạo từ vẻ đẹp nồng n&agrave;n, huyền b&iacute; v&agrave; quyến rũ nhất của ph&aacute;i đẹp - một nửa tuyệt vời của thế giới</p>\r\n', 0, 0, 20000000, 0, 0, '4_7ca6efc382c543bbac9c8c25fa199d24_compact-removebg-preview.png', '1_20ita_20m2683_compact-removebg-preview.png', '2_20ita_206552_compact-removebg-preview.png', '2022-01-20', '0000-00-00', 50, 0),
(9, 10, 'Bộ Trang Sức Ngọc Trai Vàng 14K Printemps PTB 99', 'bo-trang-suc-ngoc-trai-vang-14k-printemps-ptb-99', '<p>M&atilde; sản phẩm: PTB 99</p>\r\n', 0, 0, 13000000, 0, 0, '4_5c887c145da84d37b0c9d02d1997f55f_compact-removebg-preview.png', '3_2014k_203280_compact-removebg-preview.png', '1_2014k_203300_compact-removebg-preview.png', '2022-01-20', '0000-00-00', 50, 0),
(10, 10, 'Bộ Trang Sức ', 'bo-trang-suc-be-cool-ptb-108v', '<p>M&atilde; sản phẩm: PTB108-14K Bộ Trang Sức &quot;BE COOL&quot; PTB 108V với thiết kế trang sức tinh xảo được lấy cảm hứng chủ đạo từ vẻ đẹp nồng n&agrave;n, huyền b&iacute; v&agrave; quyến rũ nhất của ph&aacute;i đẹp - một nửa tuyệt vời của thế giới</p>\r\n', 1, 0, 12000000, 10000000, 1, '4_682ad80016624ac7841d069c515446c1_compact-removebg-preview.png', 'ptb108_compact.jpg', 'ptb108_compact.jpg', '2022-01-20', '0000-00-00', 50, 0),
(11, 11, 'Bộ Trang Sức ', 'bo-trang-suc-printemp-ptb-169', '<p>M&atilde; sản phẩm: PTB169-14K</p>\r\n', 0, 1, 13000000, 0, 0, 'ptb251__3__9577f1277b1c483db50ca5b7f263c50d_compact-removebg-preview.png', 'ptb251__1__8a6336256370445f97e5484f792fdff2_compact-removebg-preview.png', 'ptb251__4__4958545cc1e2445c92bf984823d6aa37_compact-removebg-preview.png', '2022-01-20', '0000-00-00', 50, 0),
(12, 11, 'Bộ Trang Sức Printemps PTB 120', 'bo-trang-suc-ptintemps-ptb-120', '<p>M&atilde; sản phẩm: PTB120 Bộ Trang Sức Printemps PTB 120 với h&agrave;ng loạt mẫu thiết kế trang sức tinh xảo được lấy cảm hứng chủ đạo từ vẻ đẹp nồng n&agrave;n, huyền b&iacute; v&agrave; quyến rũ nhất của ph&aacute;i đẹp - một nửa tuyệt vời của thế giới</p>\r\n', 0, 0, 17000000, 0, 0, '147_compact-removebg-preview.png', '1_36ad421d78454846b076e345cf997e55_compact-removebg-preview.png', '4_68bb51d5facc47b58235da12e643aca0_compact-removebg-preview.png', '2022-01-20', '0000-00-00', 50, 0),
(13, 11, 'Bộ Trang Sức Ruby Queen PTB 291', 'bo-trang-suc-ruby-queen-ptb-291', '<p>M&atilde; sản phẩm: PTB 291 - 14K Ruby Queen nổi bật nhất nhờ vi&ecirc;n đ&aacute; chủ m&agrave;u hồng Ruby d&aacute;ng h&igrave;nh giọt nước sang trọng. L&agrave; nữ ho&agrave;ng của thế giới đ&aacute; m&agrave;u, đ&aacute; m&agrave;u Ruby hồng tượng trưng cho t&igrave;nh y&ecirc;u thuần khiết, nồng ch&aacute;y v&agrave; đam m&ecirc; m&atilde;nh liệt.</p>\r\n', 1, 1, 20000000, 18000000, 1, 'fl_ptb309.1-02_d48caa3df1624bd180c0adde92222bff_compact.jpg', 'wb_dcptb289__1__4101ef077178487498f367e6328fd775_compact-removebg-preview.png', 'wb_btptb289__1__ec3b8a58c88d4590abb47a8e188dc39b_compact-removebg-preview.png', '2022-01-20', '0000-00-00', 50, 0),
(14, 12, 'Bộ Trang Sức Red Velvet PTB 289', 'bo-trang-suc-red-velvet-ptb-289', '<p>M&atilde; sản phẩm: PTB 289 - 14K Red Velvet l&agrave; bộ trang sức đ&aacute; m&agrave;u Ruby, với d&acirc;y chuyền d&aacute;ng d&agrave;i, b&ocirc;ng tai d&aacute;ng rơi với độ d&agrave;i vừa phải v&agrave; nhẫn d&aacute;ng three-stone ring.</p>\r\n', 0, 0, 13000000, 0, 0, 'sbzt00x000004-bong-tai-bac-dinh-da-pnjsilver-001.png', 'SBZT00X000004-SCZT00X000002-bo-trang-suc-bac-dinh-da-pnjsilver.png', 'sczt00x000002-day-co-bac-dinh-da-pnjsilver-001.png', '0000-00-00', '0000-00-00', 50, 0),
(15, 12, 'Bộ trang sức PTB 278', 'bo-trang-suc-ptb-278', '<p>M&atilde; sản phẩm: PTB278 - 14K Bộ trang sức PTB 278 nổi bật đầy sức h&uacute;t bởi vi&ecirc;n đ&aacute; aqua được đặt ở trọng t&acirc;m của mặt d&acirc;y, kết hợp c&ugrave;ng họa tiết c&aacute;nh b&ocirc;ng tuyết độc đ&aacute;o. Bạn c&oacute; thể phối set trang sức n&agrave;y c&ugrave;ng trang phục c&ocirc;ng sở, trang phục dạo phố đều</p>\r\n', 0, 0, 8000000, 5000000, 1, 'fl_ptb309.1-02_d48caa3df1624bd180c0adde92222bff_compact.jpg', 'b5_cf1e74f7eb1045528b7067f84196109e_master.webp', '1_36ad421d78454846b076e345cf997e55_compact-removebg-preview.png', '2022-01-20', '0000-00-00', 50, 0),
(16, 13, 'Bộ khuyên tai đính bi 4-5-6 mm', 'bo-khuyen-tai-dinh-bi', '<p>Những chiếc đinh t&aacute;n b&oacute;ng thời thượng n&agrave;y được chế t&aacute;c bằng v&agrave;ng v&agrave;ng 14K mịn v&agrave; c&oacute; đường k&iacute;nh từ 4 đến 6 mm để bạn c&oacute; thể t&ocirc; đậm hoặc đơn giản t&ugrave;y th&iacute;ch. Cặp nhỏ nhất c&oacute; đường k&iacute;nh 1/8 inch v&agrave; cặp lớn nhất c&oacute; đường k&iacute;nh 1/4 inch v&agrave; c&oacute; c&aacute;c b&agrave;i đăng v</p>\r\n', 1, 1, 7000000, 5000000, 1, 'finecraft-4-5-6-mm-ball-stud-earring-set-in-14k-yellow-gold-removebg-preview.png', 'finecraft-4-5-6-mm-ball-stud-earring-set-in-14k-yellow-gold-removebg-preview (1).png', 'finecraft-4-5-6-mm-ball-stud-earring-set-in-14k-yellow-gold.jpg', '2022-01-20', '0000-00-00', 50, 0),
(17, 13, 'Hoa tai chùm Topaz xanh với kim cương', 'hoa-tai-chum-topaz-xanh-voi-kim-cuong', '<p>Th&ecirc;m m&agrave;u xanh v&agrave; hợp thời trang v&agrave;o trang phục của bạn với những chiếc đinh t&aacute;n h&igrave;nh học n&agrave;y. Đ&aacute; topaz xanh Thụy Sĩ cắt h&igrave;nh bầu dục, đ&aacute; topaz xanh London cắt vu&ocirc;ng, đ&aacute; topaz xanh da trời cắt bằng đệm v&agrave; điểm nhấn bằng kim cương cắt tr&ograve;n tạo n&ecirc;n cụm</p>\r\n', 0, 0, 7000000, 0, 0, 'welry-cubic-zirconia-climber-earrings-in-14k-yellow-gold-removebg-preview.png', 'welry-cubic-zirconia-climber-earrings-in-14k-yellow-gold-removebg-preview (2).png', 'welry-cubic-zirconia-climber-earrings-in-14k-yellow-gold-removebg-preview (1).png', '2022-01-20', '0000-00-00', 50, 0),
(18, 13, 'Bông tai Huggie Ngọc trai trắng 7mm', 'bong-tai-huggie-ngoc-trai-trang-7mm', '<p>Vẻ đẹp vượt thời gian v&agrave; vẻ s&aacute;ng b&oacute;ng của ngọc trai được thể hiện đầy đủ trong những chiếc v&ograve;ng v&agrave;ng nhỏ n&agrave;y. Ch&uacute;ng được gắn những vi&ecirc;n ngọc trai nước ngọt m&agrave;u trắng lủng lẳng tr&ocirc;ng lấp l&aacute;nh v&agrave; tươi s&aacute;ng xung quanh khu&ocirc;n mặt. Đ&ocirc;i b&ocirc;ng tai bằng v&agrave;ng 1</p>\r\n', 1, 0, 5000000, 4000000, 1, 'welry-7mm-white-pearl-huggie-earrings-in-14k-yellow-gold-removebg-preview.png', 'welry-7mm-white-pearl-huggie-earrings-in-14k-yellow-gold-removebg-preview (1).png', 'welry-7mm-white-pearl-huggie-earrings-in-14k-yellow-gold.jpg', '2022-01-20', '0000-00-00', 50, 0),
(19, 14, 'Mặt dây chuyền Peridot hình bầu dục', 'mat-day-chuyen-peridot-hinh-bau-duc', '<p>Mặt d&acirc;y chuyền tuyệt vời n&agrave;y sẽ ph&ugrave; hợp với tất cả c&aacute;c ngoại h&igrave;nh của bạn! Những vi&ecirc;n đ&aacute; qu&yacute; sapphire xanh lam cắt tr&ograve;n l&agrave;m đường viền cho chiếc v&ograve;ng cổ dạng thanh n&agrave;y c&oacute; tổng trọng lượng l&agrave; 1/2 ct v&agrave; được chế t&aacute;c bằng v&agrave;ng v&agrave;ng 10K. Mảnh c&oacute; chiều d&agrave;</p>\r\n', 1, 0, 9000000, 7000000, 1, 'welry-natural-oval-swiss-blue-topaz-pendant-in-10k-white-gold-17-removebg-preview.png', 'welry-natural-oval-swiss-blue-topaz-pendant-in-10k-white-gold-17-removebg-preview (1).png', 'welry-natural-oval-swiss-blue-topaz-pendant-in-10k-white-gold-17.jpg', '2022-01-20', '0000-00-00', 50, 0),
(20, 14, 'Dây chuyền đá Topaz màu trắng và ngọc lục bảo', 'day-chuyen-da-topaz-mau-trang-va-ngoc-luc-bao', '<p>H&atilde;y thắp s&aacute;ng bất kỳ bộ trang phục n&agrave;o với chiếc v&ograve;ng cổ tuyệt đẹp n&agrave;y. Mặt d&acirc;y chuyền n&agrave;y c&oacute; c&aacute;c vi&ecirc;n đ&aacute; ngọc lục bảo tự nhi&ecirc;n được cắt h&igrave;nh tr&ograve;n được l&agrave;m nổi bật bởi c&aacute;c vi&ecirc;n topaz trắng tự nhi&ecirc;n tr&ecirc;n một v&ograve;ng tr&ograve;n mở. Được chế t&aacute;c từ v&agrave;ng v&agrave;ng 14K</p>\r\n', 0, 0, 10000000, 8000000, 1, 'welry-swiss-blue-topaz-and-white-topaz-circle-pendant-in-14k-yellow-gold-18-removebg-preview.png', '', 'welry-swiss-blue-topaz-and-white-topaz-circle-pendant-in-14k-yellow-gold-18.jpg', '2022-01-20', '0000-00-00', 50, 0),
(21, 14, 'Dây chuyền mặt kim cương', 'day-chuyen-mat-kim-cuong', '<p>Mang t&iacute;nh biểu tượng v&agrave; tinh thần, mặt d&acirc;y chuyền chữ thập n&agrave;y c&oacute; một lớp trang điểm bắt mắt. N&oacute; được xếp ho&agrave;n to&agrave;n bằng những vi&ecirc;n kim cương cắt tr&ograve;n v&agrave; được treo tr&ecirc;n một d&acirc;y chuyền bạc sterling cổ điển. H&atilde;y biến n&oacute; th&agrave;nh t&aacute;c phẩm h&agrave;ng ng&agrave;y của bạn</p>\r\n', 1, 1, 15000000, 14000000, 1, 'welry-cross-pendant-necklace-with-diamonds-in-sterling-silver-17-+-1-removebg-preview.png', 'welry-cross-pendant-necklace-with-diamonds-in-sterling-silver-17-+-1.jpg', 'welry-cross-pendant-necklace-with-diamonds-in-sterling-silver-17-+-1 (1).jpg', '2022-01-20', '0000-00-00', 50, 0),
(22, 15, 'Vòng đeo tay liên kết kẹp giấy', 'vong-tay-lien-ket-kep-giay', '<p>Một m&oacute;n đồ trang sức kh&ocirc;ng bao giờ lỗi mốt. L&agrave;m tr&ograve;n bộ sưu tập của bạn với v&ograve;ng đeo tay h&igrave;nh học n&agrave;y &ndash; c&aacute;c mắt x&iacute;ch h&igrave;nh bầu dục rỗng được chế t&aacute;c bằng v&agrave;ng 14K v&agrave; c&oacute; lớp ho&agrave;n thiện đ&aacute;nh b&oacute;ng. Mảnh c&oacute; chiều d&agrave;i 7 1/2 inch v&agrave; c&oacute; m&oacute;c c&agrave;i h&igrave;nh con t&ocirc;m</p>\r\n', 1, 0, 6000000, 5000000, 1, 'welry-chain-link-band-ring-in-14k-yellow-gold-and-rhodium-plating-size-8-removebg-preview.png', 'welry-chain-link-band-ring-in-14k-yellow-gold-and-rhodium-plating-size-8-removebg-preview (1).png', 'welry-chain-link-band-ring-in-14k-yellow-gold-and-rhodium-plating-size-8-removebg-preview (2).png', '2022-01-20', '0000-00-00', 50, 0),
(23, 15, 'Vòng đeo tay tennis Bolo với Zirconias', 'vong-tay-tennis-bolo-voi-zirconias', '<p>Vẫn sang trọng nhưng vẫn thoải m&aacute;i với v&ograve;ng đeo tay zirconia h&igrave;nh khối phong c&aacute;ch bolo n&agrave;y! N&oacute; mang lại cho bạn tất cả sự h&agrave;o nho&aacute;ng v&agrave; quyến rũ m&agrave; bạn c&oacute; thể muốn c&oacute; trong một chiếc v&ograve;ng tay, ho&agrave;n hảo cho một buổi tối đặc biệt hoặc chỉ trong tuần l&agrave;</p>\r\n', 0, 1, 5000000, 4000000, 1, 'welry-tennis-bolo-bracelet-with-cubic-zirconias-in-rhodium-plated-brass-10-removebg-preview.png', 'welry-tennis-bolo-bracelet-with-cubic-zirconias-in-rhodium-plated-brass-10-removebg-preview (1).png', 'welry-tennis-bolo-bracelet-with-cubic-zirconias-in-rhodium-plated-brass-10-removebg-preview (2).png', '2022-01-20', '0000-00-00', 50, 0),
(24, 15, 'Vòng tay đá mắt hổ nam', 'vong-tay-mat-ho-nam', '<p>Anh ấy sẽ th&iacute;ch vẻ ngo&agrave;i nam t&iacute;nh n&agrave;y với chiếc v&ograve;ng tay đ&aacute; mắt hổ tự nhi&ecirc;n n&agrave;y. Đ&ocirc;i mắt hổ s&aacute;ng b&oacute;ng tạo ra một sự tương phản tuyệt đẹp với một chiếc kẹp c&agrave;ng t&ocirc;m h&ugrave;m lớn bằng th&eacute;p kh&ocirc;ng gỉ. Mang lại sự trở lại của những năm 90 với chiếc v&ograve;ng cổ điể</p>\r\n', 0, 0, 3000000, 0, 0, 'mens-10-mm-natural-tigers-eye-bracelet-in-stainless-steel-8-removebg-preview.png', 'mens-10-mm-natural-onyx-beaded-bracelet-in-stainless-steel-8-removebg-preview.png', 'mens-10-mm-natural-onyx-beaded-bracelet-in-stainless-steel-8-removebg-preview.png', '2022-01-20', '0000-00-00', 50, 0),
(25, 16, 'Nhẫn ngọc lục bảo ', 'nhan-ngoc-luc-bao', '<p>H&atilde;y kết hợp chiếc nhẫn đơn giản nhưng sang trọng n&agrave;y với bất kỳ trang phục n&agrave;o. Với vi&ecirc;n đ&aacute; trung t&acirc;m ngọc lục bảo tự nhi&ecirc;n cắt 1/3 ct emerald được viền bằng 1/10 cttw kim cương cắt tr&ograve;n tự nhi&ecirc;n, chiếc nhẫn v&agrave;ng 14K m&agrave;u v&agrave;ng n&agrave;y l&agrave; sự lựa chọn ho&agrave;n</p>\r\n', 0, 1, 4000000, 0, 0, 'welry-pear-cut-pink-tourmaline-beaded-ring-in-10k-yellow-gold-size-7-removebg-preview.png', 'welry-pear-cut-pink-tourmaline-beaded-ring-in-10k-yellow-gold-size-7-removebg-preview (1).png', 'welry-pear-cut-pink-tourmaline-beaded-ring-in-10k-yellow-gold-size-7-removebg-preview (2).png', '2022-01-20', '0000-00-00', 50, 0),
(26, 16, 'Nhẫn Sapphire trắng với kim cương', 'nhan-sapphire-trang-voi-kim-cuong', '<p>Đ&aacute; chiếc nhẫn sang trọng n&agrave;y c&oacute; một vi&ecirc;n đ&aacute; sinh th&aacute;ng Tư. Được chế t&aacute;c bằng v&agrave;ng trắng 10K, chiếc nhẫn n&agrave;y c&oacute; vi&ecirc;n đ&aacute; sapphire trắng cắt h&igrave;nh bầu dục ở t&acirc;m 2 1/2 ct với những vi&ecirc;n đ&aacute; tạo điểm nhấn bằng kim cương cắt tr&ograve;n tr&ecirc;n vai. Mặt nhẫn c&oacute; chiều</p>\r\n', 1, 0, 8000000, 7000000, 1, '1-ct-diamond-pear-shaped-engagement-ring-in-10k-white-gold-removebg-preview.png', '1-ct-diamond-pear-shaped-engagement-ring-in-10k-white-gold-removebg-preview (1).png', '1-ct-diamond-pear-shaped-engagement-ring-in-10k-white-gold-removebg-preview (2).png', '2022-01-20', '0000-00-00', 49, 0),
(27, 16, 'Bộ nhẫn Tanzanite & Zirconia khối', 'bo-nhan-khoi-tanzanite-zirconia-khoi', '<p>Nếu bạn c&oacute; một người phụ nữ đặc biệt trong cuộc đời m&igrave;nh, th&igrave; đ&acirc;y l&agrave; set đồ c&ocirc; d&acirc;u ph&ugrave; hợp cho c&ocirc; ấy. Đặc biệt nếu m&agrave;u sắc y&ecirc;u th&iacute;ch của c&ocirc; ấy l&agrave; m&agrave;u t&iacute;m, th&igrave; phần trung t&acirc;m m&agrave;u tanzanite s&aacute;ng sẽ l&agrave;m nổi bật đ&ocirc;i mắt lấp l&aacute;nh của c&ocirc; ấy. Một vi&ecirc;n đ&aacute; tr</p>\r\n', 0, 1, 9000000, 8000000, 1, 'simulated-tanzanite-and-cubic-zirconia-ring-set-in-14k-gold-plated-sterling-silver-removebg-preview.png', 'simulated-tanzanite-and-cubic-zirconia-ring-set-in-14k-gold-plated-sterling-silver-removebg-preview (1).png', 'simulated-tanzanite-and-cubic-zirconia-ring-set-in-14k-gold-plated-sterling-silver-removebg-preview (2).png', '2022-01-20', '0000-00-00', 50, 0),
(28, 16, 'Nhẫn Morganite & Tanzanite đính kim cương', 'nhan-morganite-tanzanite-dinh-kim-cuong', '<p>V&agrave;ng hồng 10K hiện đại l&agrave; sự kết hợp ho&agrave;n hảo cho chiếc nhẫn c&oacute; m&agrave;u hồng phấn n&agrave;y! Một morganite tự nhi&ecirc;n được cắt h&igrave;nh bầu dục ở trung t&acirc;m được chiếu s&aacute;ng bởi c&aacute;c h&agrave;ng tanzanite m&agrave;u t&iacute;m nhạt, tổng cộng 1 3/8 ct v&agrave; được tạo điểm nhấn bằng những vi&ecirc;n</p>\r\n', 1, 0, 6000000, 0, 0, 'london-and-swiss-blue-topaz-ring-with-diamond-accents-in-10k-gold-removebg-preview.png', 'london-and-swiss-blue-topaz-ring-with-diamond-accents-in-10k-gold-removebg-preview (1).png', 'london-and-swiss-blue-topaz-ring-with-diamond-accents-in-10k-gold-removebg-preview (2).png', '2022-01-20', '0000-00-00', 49, 0),
(29, 8, 'Nhẫn đôi kim cương', 'nhan-doi-kim-cuong', '<p>Một chiếc nhẫn d&acirc;y trang nh&atilde; được nạm những vi&ecirc;n kim cương lấp l&aacute;nh tuyệt đẹp. Mua c&aacute;i n&agrave;y cho ch&iacute;nh m&igrave;nh hoặc tặng c&aacute;i n&agrave;y cho người đặc biệt trong cuộc đời của bạn! Những vi&ecirc;n kim cương tr&ograve;n cắt tr&ograve;n c&oacute; tổng cộng 1/4 ct d&ograve;ng nhẫn v&agrave;ng 10K n&agrave;y. D&acirc;y</p>\r\n', 0, 1, 7000000, 5000000, 1, '1-3-ct-diamond-triple-row-band-ring-in-10k-gold-removebg-preview.png', '1-3-ct-diamond-triple-row-band-ring-in-10k-gold-removebg-preview (1).png', '1-3-ct-diamond-triple-row-band-ring-in-10k-gold-removebg-preview (2).png', '2022-01-19', '0000-00-00', 50, 0),
(30, 13, 'Hoa tai Kim cương Zig Zag Hoop ', 'hoa-tai-kim-cuong-zig-zag-hoop', '<p>VỀ SẢN PHẨM N&Agrave;Y Th&ecirc;m một số s&aacute;ng tạo cho trang phục của bạn với những b&ocirc;ng tai v&ograve;ng vui nhộn n&agrave;y! Kiểu v&ograve;ng zig zag độc đ&aacute;o được đ&iacute;nh những vi&ecirc;n kim cương cắt tr&ograve;n c&oacute; tổng trọng lượng 1/4 ct v&agrave; được chế t&aacute;c bằng v&agrave;ng v&agrave;ng 14K. C&aacute;c miếng c&oacute; k&iacute;ch thướ</p>\r\n', 1, 0, 7000000, 0, 0, 'ohmy-diamonds-1-4-cttw-diamond-zig-zag-hoop-earrings-in-14k-yellow-gold-removebg-preview.png', 'ohmy-diamonds-1-4-cttw-diamond-zig-zag-hoop-earrings-in-14k-yellow-gold-removebg-preview (1).png', 'ohmy-diamonds-1-4-cttw-diamond-zig-zag-hoop-earrings-in-14k-yellow-gold.jpg', '2022-01-20', '0000-00-00', 49, 0),
(31, 13, 'Hoa tai hình bầu dục Sapphire xanh dương với kim cương', 'hoa-tai-hinh-bau-duc-sapphire-xanh-duong-voi-kim-cuong', '<p>H&atilde;y l&agrave;m say l&ograve;ng mọi người bằng những chiếc đinh t&aacute;n tuyệt đẹp n&agrave;y to&aacute;t l&ecirc;n vẻ nữ t&iacute;nh quyến rũ. Th&ecirc;m sắc xanh v&agrave;o trang phục của bạn với đ&ocirc;i b&ocirc;ng tai v&agrave;ng trắng 10K n&agrave;y c&oacute; tổng cộng 4 vi&ecirc;n đ&aacute; sapphire xanh lam được cắt h&igrave;nh bầu dục 3/8 ct được bao q</p>\r\n', 1, 0, 10000000, 7000000, 1, 'honora-freshwater-pearl-and-cubic-zirconia-drop-earrings-in-sterling-silver-removebg-preview.png', 'honora-freshwater-pearl-and-cubic-zirconia-drop-earrings-in-sterling-silver-removebg-preview (1).png', 'honora-freshwater-pearl-and-cubic-zirconia-drop-earrings-in-sterling-silver.jpg', '2022-01-20', '0000-00-00', 48, 0),
(32, 13, 'Bông tai vòng tròn', 'bong-tai-vong-tron', '<p>Ho&agrave;n thiện bất kỳ bộ quần &aacute;o n&agrave;o với đ&ocirc;i b&ocirc;ng tai v&ograve;ng hiện đại n&agrave;y. Những chiếc v&ograve;ng hiện đại n&agrave;y sẽ trở th&agrave;nh phụ kiện mới vượt thời gian của bạn, được chế t&aacute;c với chất lượng v&agrave; độ ch&iacute;nh x&aacute;c bằng hợp kim v&agrave;ng 1K mạ v&agrave;ng 14K mịn. C&aacute;c mảnh c&oacute; k&iacute;ch th</p>\r\n', 0, 1, 5000000, 0, 0, 'circle-frame-infinity-hoop-earrings-in-14k-gold-plated-1k-gold-alloy-removebg-preview.png', 'circle-frame-infinity-hoop-earrings-in-14k-gold-plated-1k-gold-alloy.jpg', 'circle-frame-infinity-hoop-earrings-in-14k-gold-plated-1k-gold-alloy (1).jpg', '2022-01-20', '0000-00-00', 50, 0),
(33, 13, 'Bông tai đính kim cương 1/4', 'bong-tai-dinh-da-kim-cuong-1/4', '<p>Một thiết kế b&ocirc;ng tai kết hợp h&igrave;nh học kh&ocirc;ng giống như những b&ocirc;ng tai đinh t&aacute;n kh&aacute;c của bạn. H&igrave;nh b&aacute;t gi&aacute;c c&oacute; nhiều vi&ecirc;n kim cương cắt tr&ograve;n c&oacute; k&iacute;ch thước bằng 1/4 ct v&agrave; được chế t&aacute;c bằng v&agrave;ng v&agrave;ng 14K. C&aacute;c mảnh c&oacute; k&iacute;ch thước 1/4 x 1/4 inch. 1/4 vi&ecirc;n</p>\r\n', 0, 1, 5000000, 0, 0, 'ohmy-diamonds-1-4-cttw-diamond-stud-earrings-in-14k-yellow-gold-removebg-preview.png', 'ohmy-diamonds-1-4-cttw-diamond-stud-earrings-in-14k-yellow-gold-removebg-preview (1).png', 'ohmy-diamonds-1-4-cttw-diamond-stud-earrings-in-14k-yellow-gold.jpg', '2022-01-20', '0000-00-00', 50, 0),
(34, 14, 'Chuỗi vòng cổ Figaro', 'chuoi-vong-co-figaro', '<p>Một chiếc v&ograve;ng cổ dạng chuỗi figaro được đ&aacute;nh b&oacute;ng cổ điển c&oacute; trọng lượng nhẹ. Ho&agrave;n hảo để đeo một m&igrave;nh hoặc kết hợp với nhiều v&ograve;ng cổ hơn, n&oacute; được chế t&aacute;c bằng v&agrave;ng v&agrave;ng 14K lấp l&aacute;nh. Mảnh c&oacute; chiều d&agrave;i 22 inch. Sản xuất tại &Yacute;. Thiết kế chuỗi 3 + 1</p>\r\n', 1, 0, 7000000, 5000000, 1, 'italian-made-figaro-chain-necklace-in-14k-gold-22-removebg-preview.png', 'italian-made-figaro-chain-necklace-in-14k-gold-22-removebg-preview (1).png', 'italian-made-figaro-chain-necklace-in-14k-gold-22-removebg-preview (2).png', '2022-01-20', '0000-00-00', 48, 0),
(35, 14, 'Dây chuyền mặt trái tim hình trái tim bằng ngọc trai', 'day-chuyen-mat-trai-tim-bang-ngoc-trai', '<p>Đ&aacute; chiếc v&ograve;ng cổ xinh đẹp n&agrave;y với bất kỳ &aacute;nh nh&igrave;n n&agrave;o. Chiếc v&ograve;ng cổ n&agrave;y c&oacute; mặt d&acirc;y chuyền bằng ngọc trai cắt tr&ograve;n nạm đ&aacute; sapphire hồng tạo điểm nhấn tạo th&agrave;nh h&igrave;nh tr&aacute;i tim để biểu thị t&igrave;nh y&ecirc;u của bạn. Đi k&egrave;m với một chuỗi d&acirc;y 18 inch với một chốt</p>\r\n', 0, 1, 8000000, 0, 0, 'natural-mother-of-pearl-and-created-pink-sapphire-circle-heart-pendant-in-18k-gold-plated-sterling-silver-18-removebg-preview.png', 'natural-mother-of-pearl-and-created-pink-sapphire-circle-heart-pendant-in-18k-gold-plated-sterling-silver-18-removebg-preview (1).png', 'natural-mother-of-pearl-and-created-pink-sapphire-circle-heart-pendant-in-18k-gold-plated-sterling-silver-18.jpg', '0000-00-00', '0000-00-00', 50, 0),
(36, 14, 'Vòng cổ trạm hạt hai lớp', 'vong-co-tram-hat-hai-lop', '<p>Một cụm gồm hai chuỗi với c&aacute;c trạm cườm lấp l&aacute;nh tr&ecirc;n cổ của bạn. N&oacute; tạo ra một c&aacute;i nh&igrave;n hợp xu hướng, c&oacute; nhiều lớp để mặc với quần jean v&agrave; &aacute;o ph&ocirc;ng y&ecirc;u th&iacute;ch của bạn. V&ograve;ng cổ c&oacute; chiều d&agrave;i 18 inch, c&oacute; m&oacute;c c&agrave;i v&ograve;ng l&ograve; xo. Tại Welry, ch&uacute;ng t&ocirc;i tin tưởn</p>\r\n', 0, 1, 10000000, 0, 0, 'welry-double-layer-bead-station-necklace-in-14k-yellow-gold-18-removebg-preview.png', 'welry-double-layer-bead-station-necklace-in-14k-yellow-gold-18-removebg-preview (1).png', 'welry-double-layer-bead-station-necklace-in-14k-yellow-gold-18.jpg', '2022-01-20', '0000-00-00', 50, 0),
(37, 15, 'Vòng đeo tay linh hoạt hạt', 'vong-tay-linh-hoat-hat', '<p>H&atilde;y thể hiện phong c&aacute;ch b&oacute;ng bẩy của bạn với chiếc &aacute;o măng t&ocirc; xinh xắn n&agrave;y. V&ograve;ng đeo tay bằng v&agrave;ng 14K n&agrave;y c&oacute; một hạt 3,5 mm ở một đầu v&agrave; một hạt c&oacute; thể th&aacute;o rời ở đầu kia để dễ d&agrave;ng đeo. Mảnh c&oacute; chu vi 6 1/2 inch. V&ograve;ng đeo tay Thiết kế linh hoạt</p>\r\n', 1, 0, 9000000, 8500000, 1, 'bead-flexible-bangle-bracelet-in-14k-gold-6-5-removebg-preview.png', 'bead-flexible-bangle-bracelet-in-14k-gold-6-5-removebg-preview (1).png', 'bead-flexible-bangle-bracelet-in-14k-gold-6-5-removebg-preview (1).png', '2022-01-20', '0000-00-00', 49, 0),
(38, 15, 'Vòng tay charm ngọc trai trắng với kim cương', 'vong-tay-charm-ngoc-trai-trang-voi-kim-cuong', '<p>V&ograve;ng đeo tay bằng v&agrave;ng 14K m&agrave;u v&agrave;ng b&oacute;ng n&agrave;y c&oacute; mũi t&ecirc;n nhỏ v&agrave; những vi&ecirc;n ngọc trai b&oacute;ng bẩy, l&agrave;m cho n&oacute; trở th&agrave;nh một lựa chọn tuyệt vời để phối với c&aacute;c phong c&aacute;ch kh&aacute;c. V&ograve;ng tay n&agrave;y c&oacute; ngọc trai nước ngọt 6-6,5 mm v&agrave; c&aacute;c điểm nhấn bằng kim cương cắ</p>\r\n', 0, 1, 1100000, 0, 0, '5-6-mm-freshwater-pearl-bracelet-in-14k-gold-removebg-preview.png', 'honora-7-8-mm-freshwater-pearl-bracelet-in-sterling-silver-7-removebg-preview (1).png', 'honora-7-8-mm-freshwater-pearl-bracelet-in-sterling-silver-7-removebg-preview.png', '2022-01-20', '0000-00-00', 49, 0),
(39, 15, 'Vòng tay ngọc trai nước ngọt trắng', 'vong-tay-ngoc-trai-nuoc-ngot-trang', '<p>L&agrave;m nổi bật phong c&aacute;ch của bạn với v&ograve;ng b&iacute;t kim loại hỗn hợp n&agrave;y. N&oacute; được đ&uacute;c bằng bạc sterling c&oacute; kết cấu với ngọc trai v&agrave; v&agrave;ng 14K, một thiết kế linh hoạt ph&ugrave; hợp với bất kỳ m&oacute;n đồ trang sức n&agrave;o kh&aacute;c m&agrave; bạn kết hợp với n&oacute;. V&ograve;ng đeo tay n&agrave;y c&oacute; mặt s</p>\r\n', 0, 1, 9000000, 8000000, 1, 'honora-7-8-mm-freshwater-pearl-bracelet-in-sterling-silver-7-removebg-preview.png', 'honora-7-8-mm-freshwater-pearl-bracelet-in-sterling-silver-7-removebg-preview (1).png', '5-6-mm-freshwater-pearl-bracelet-in-14k-gold-removebg-preview.png', '2022-01-20', '0000-00-00', 50, 0);

